import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

import reportWebVitals from './reportWebVitals';
import { MkForm } from './MkForm';

const initialValues = {
	name: '',
	email: '',
	message: ''
};

const errors = {
	nameError: '',
	emailError: '',
	messageError: ''
};

ReactDOM.render(
  <React.StrictMode>

    <div style={{ textAlign: "center" }}>
      <MkForm
        onSubmit={({name, email, msg}) => {
          fetch('https://yugf011gw6.execute-api.us-east-2.amazonaws.com/staging', {method: 'POST', headers: {'Accept': 'application/json', 'Content-Type': 'application/json'}, body: JSON.stringify({ name, msg, email })})
        }} />
    </div>

  </React.StrictMode>,
  document.getElementById('root')
);

export default function sendEmail (subject, body, email) {
  return fetch('https://yugf011gw6.execute-api.us-east-2.amazonaws.com/staging', {
    method: 'POST',
    headers: {
      'Accept': 'application/json',
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({ subject, body, email })
  })
}

const handleSubmit = e => {
  e.preventDefault();
  const data = {
    body : {
      name: MkForm.name,
      email: MkForm.body,
      message: sendEmail.email
    }
  }
  API.post('mkdecisionapi','/contact',data)		
  setFormValues(initialValues);
  setConfirmed(true);
  setOpen(false);
};

// If you want to start measuring performance in your app, pass a function
